import React, { useState, useEffect } from "react";
import axios from 'axios';
import './Settings.css';
const Settings = () => {


  const [profile, setProfile] = useState({
    name: '',
    mobile:'',
    password: '',
    address: ''
  });

  const [isFetching, setIsFetching] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  // Function to fetch profile data
  const fetchProfile = (id) => {
    const email= localStorage.getItem('email');
    setIsFetching(true);
    axios.get('http://localhost:8080/api/profile/${email}')
      .then(response => {
        setProfile(response.data);
        setIsFetching(false);
      })
      .catch(error => {
        console.error('Error fetching profile:', error);
        setIsFetching(false);
      });
  };

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSubmitting(true);
    axios.post('http://localhost:8080/api/profile', profile)
      .then(response => {
        setMessage('Profile submitted successfully!');
        setIsSubmitting(false);
      })
      .catch(error => {
        console.error('Error submitting profile:', error);
        setMessage('Error submitting profile.');
        setIsSubmitting(false);
      });
  };

  // Function to handle input changes
  const handleChange = (event) => {
    const { name, value } = event.target;
    setProfile({ ...profile, [name]: value });
  };

  useEffect(() => {
    // Fetch profile data for a specific id, for example id 1
    fetchProfile(1);
  }, []);

  return (
    <div>
            <h2>Profile Form</h2>
            {isFetching ? <p>Loading...</p> : null}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>ID: </label>
                    <input type="text" name="id" value={profile.id} onChange={handleChange} readOnly />
                </div>
                <div>
                    <label>Name: </label>
                    <input type="text" name="name" value={profile.name} onChange={handleChange} required />
                </div>
                <div>
                    <label>Email: </label>
                    <input type="email" name="email" value={profile.email} onChange={handleChange} required />
                </div>
                <div>
                    <label>Address: </label>
                    <input type="text" name="address" value={profile.address} onChange={handleChange} required />
                </div>
                <div>
                    <button type="submit" disabled={isSubmitting}>Submit</button>
                </div>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}
export default Settings;
