import React, { useState, useEffect } from "react";
import axios from 'axios';

const Settings = () => {


    const [name, setName] = useState('');
    const [mobile, setMobile] = useState('');
    const [isEditingName, setIsEditingName] = useState(false);
    const [isEditingMobile, setIsEditingMobile] = useState(false);

    useEffect(() => {
        // Fetch user data from the backend
        axios.get('/api/user/settings')
            .then(response => {
                setName(response.data.name);
                setMobile(response.data.mobile);
            })
            .catch(error => console.error('There was an error fetching the data!', error));
    }, []);

    const handleNameChange = (event) => setName(event.target.value);
    const handleMobileChange = (event) => setMobile(event.target.value);

    const handleSubmit = () => {
        const updatedData = { name, mobile };
        axios.post('/api/user/settings', updatedData)
            .then(response => {
                console.log('Data successfully updated');
                setIsEditingName(false);
                setIsEditingMobile(false);
            })
            .catch(error => console.error('There was an error updating the data!', error));
    };

    return (
        <div>
            <h1>Settings</h1>
            <div>
                <label>
                    Name:
                    {isEditingName ? (
                        <input type="text" value={name} onChange={handleNameChange} />
                    ) : (
                        <span>{name}</span>
                    )}
                    <button onClick={() => setIsEditingName(!isEditingName)}>
                        {isEditingName ? 'Save' : 'Edit'}
                    </button>
                </label>
            </div>
            <div>
                <label>
                    Mobile:
                    {isEditingMobile ? (
                        <input type="text" value={mobile} onChange={handleMobileChange} />
                    ) : (
                        <span>{mobile}</span>
                    )}
                    <button onClick={() => setIsEditingMobile(!isEditingMobile)}>
                        {isEditingMobile ? 'Save' : 'Edit'}
                    </button>
                </label>
            </div>
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
};
export default Settings;
