import React, { useState, useEffect } from "react";
import axios from 'axios';
import './Settings.css';
const Settings = () => {

  const email = localStorage.getItem('email');
  const [profile, setProfile] = useState({
    name: '',
    mobile: '',
    password: '',
    address: '',

  });
  const [isFetching, setIsFetching] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  // Function to fetch profile data
  // const fetchProfile = (id) => {
  //   setIsFetching(true);
  //   axios.get('http://localhost:8080/api/get_profile',{ email: email })
  //     .then(response => {
  //       setProfile(response.data);
  //       setIsFetching(false);
  //     })
  //     .catch(error => {
  //       console.error('Error fetching profile:', error);
  //       setIsFetching(false);
  //     });
  // };
  useEffect(() => {
    axios.get('http://localhost:8080/api/get_profile/${email}')
        .then(response => {
            setProfile(response.data);
        })
        .catch(error => {
            alert(error);
            console.error("There was an error fetching the data", error);
        });
}, []);

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSubmitting(true);
    axios.post('http://localhost:8080/api/profile', profile)
      .then(response => {
        setMessage('Profile submitted successfully!');
        setIsSubmitting(false);
      })
      .catch(error => {
        console.error('Error submitting profile:', error);
        setMessage('Error submitting profile.');
        setIsSubmitting(false);
      });
  };

  // Function to handle input changes
  const handleChange = (event) => {
    const { name, value } = event.target;
    setProfile({ ...profile, [name]: value });
  };

  // useEffect(() => {
  //   // Fetch profile data for a specific id, for example id 1
  //   fetchProfile(1);
  // }, []);

  return (
    <div className="form-container">
      <h2>Profile Form</h2>
      {isFetching ? <p>Loading...</p> : null}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name: </label>
          <input type="text" name="name" value={profile.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Mobile: </label>
          <input type="text" name="mobile" value={profile.mobile} onChange={handleChange} required />
        </div>
        <div>
          <label>Password: </label>
          <input type="password" name="password" value={profile.password} onChange={handleChange} required />
        </div>
        <div>
          <label>Address: </label>
          <input type="text" name="address" value={profile.address} onChange={handleChange} required />
        </div>
        <div>
          <button type="submit" disabled={isSubmitting}>Submit</button>
        </div>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}
export default Settings;
